CREATE TABLE `site`.`membre` ( `id_membre` INT(3) NOT NULL AUTO_INCREMENT , `pseudo` VARCHAR(20) NOT NULL , `mdp` VARCHAR(32) NOT NULL , `nom` VARCHAR(20) NOT NULL , `prenom` VARCHAR(20) NOT NULL , `email` VARCHAR(50) NOT NULL , `civilite` ENUM('m','f') NOT NULL , `ville` VARCHAR(20) NOT NULL , `code_postal` INT(5) NOT NULL , `adresse` VARCHAR(50) NOT NULL , `statut` INT(1) NOT NULL , PRIMARY KEY (`id_membre`)) ENGINE = InnoDB CHARSET=utf8 COLLATE utf8_general_ci;
CREATE TABLE `site`.`produit` ( `id_produit` INT(3) NOT NULL AUTO_INCREMENT , `reference` VARCHAR(20) NOT NULL , `categorie` VARCHAR(20) NOT NULL , `titre` VARCHAR(100) NOT NULL , `description` TEXT NOT NULL , `couleur` VARCHAR(20) NOT NULL , `taille` VARCHAR(5) NOT NULL , `public` ENUM('m','f','mixte') NOT NULL , `photo` VARCHAR(250) NOT NULL , `prix` INT(3) NOT NULL , `stock` INT(3) NOT NULL , PRIMARY KEY (`id_produit`)) ENGINE = InnoDB CHARSET=utf8 COLLATE utf8_general_ci;
CREATE TABLE `site`.`details_commande` ( `id_details_commande` INT(3) NOT NULL AUTO_INCREMENT , `id_commande` INT(3) NOT NULL , `id_produit` INT(3) NOT NULL , `quantite` INT(3) NOT NULL , `prix` INT(3) NOT NULL , PRIMARY KEY (`id_details_commande`)) ENGINE = InnoDB CHARSET=utf8 COLLATE utf8_general_ci;
CREATE TABLE `site`.`commande` ( `id_commande` INT(3) NOT NULL AUTO_INCREMENT , `id_membre` INT(3) NOT NULL , `montant` INT(3) NOT NULL , `date_enregistrement` DATETIME NOT NULL , `etat` ENUM('en cours de traitement','envoyé','livré') NOT NULL , PRIMARY KEY (`id_commande`)) ENGINE = InnoDB CHARSET=utf8 COLLATE utf8_general_ci;